<?php $education=require __DIR__.'/../data/education.php'; $experience=require __DIR__.'/../data/experience.php'; $skills=require __DIR__.'/../data/skills.php'; ?>
<section class="card"><h1>Resume</h1><p class="muted">Education, experience, and skills.</p></section>
<section class="grid two"><div class="card"><h2>Education</h2><ul class="timeline"><?php foreach($education as $ed): ?><li><div class="time-title"><?php echo e($ed['degree']);?></div><div class="time-sub"><?php echo e($ed['institution']);?> • <?php echo e($ed['years']);?></div><div class="time-desc"><?php echo e($ed['details']);?></div></li><?php endforeach;?></ul></div>
<div class="card"><h2>Experience</h2><ul class="timeline"><?php foreach($experience as $ex): ?><li><div class="time-title"><?php echo e($ex['role']);?></div><div class="time-sub"><?php echo e($ex['company']);?> • <?php echo e($ex['years']);?></div><div class="time-desc"><?php echo e($ex['details']);?></div></li><?php endforeach;?></ul></div></section>
<section class="card"><h2>My Skills</h2><?php foreach($skills as $sk): $pct=max(0,min(100,(int)$sk['level'])); ?><div class="skill"><div class="skill-row"><span><?php echo e($sk['name']);?></span><span class="muted"><?php echo $pct;?>%</span></div><div class="bar"><span style="width:<?php echo $pct;?>%"></span></div></div><?php endforeach;?></section>
